import osimport openaifrom dotenv import load_dotenv# Load your API key from .env fileload_dotenv()api_key = os.getenv("OPENAI_API_KEY")# Create a client using the new v1.x APIclient = openai.OpenAI(api_key=api_key)def explain_trade(ticker, option_type, strike_price, current_price, expiry_date):    prompt = (        f"Explain a {option_type.lower()} option trade on {ticker} with a strike price of "        f"${strike_price}, current stock price of ${current_price}, expiring on {expiry_date}. "        f"Describe the potential upside, risk, and what scenarios lead to a profit or loss. "        f"Keep the explanation simple and educational."    )    try:        response = client.chat.completions.create(            model="gpt-3.5-turbo",  # or "gpt-4" if enabled on your account            messages=[                {"role": "system", "content": "You are a financial educator."},                {"role": "user", "content": prompt}            ],            temperature=0.7        )        return response.choices[0].message.content    except Exception as e:        return f"Error from OpenAI: {str(e)}"