import yfinance as yfimport numpy as npimport pandas as pdfrom datetime import datetime, timedeltadef get_stock_data(ticker):        end = datetime.today()    start = end - timedelta(days=365)    try:        data = yf.download(ticker, start=start, end=end)        # Check if empty        if data.empty:            raise ValueError(f"No data found for ticker '{ticker}'. Please try a different symbol.")        return data    except Exception as e:        print(f"[ERROR] Failed to retrieve data for {ticker}: {e}")        return Nonedef simulate_future_prices(last_price, days=30):        simulated = [last_price]    # Generate future prices using random percentage changes    for _ in range(days):        change = np.random.normal(loc=0, scale=2)  # 2% daily volatility        new_price = simulated[-1] * (1 + change / 100)        simulated.append(new_price)    future_dates = pd.date_range(datetime.today(), periods=days+1).tolist()    return pd.Series(simulated, index=future_dates)